	public int getProfData(String profession) {
		
		switch (profession) {
		
			case SPY:
				return 0x23;
			case SMUGGLER:
				return 0x19;
			case OFFICER:
				return 0x0F;
			case JEDI:
				return 0x28;
			case COMMANDO:
				return 0x1E;
			case ENTERTAINER:
				return 0x05;
			case MEDIC:
				return 0x0A;
			case BOUNTYHUNTER:
				return 0x14;
			default:
				return 0x00;
				
		}
	}

